has_cl			= lambda d: any([t for t in d if t.dep_ in ('xcomp','ccomp','mark','csubj','relcl','pcomp')])
cl_num			= lambda ess: len([d for d in ess['docs'] if has_cl(d)])
snt_spell_err	= lambda mkf: len( {v.get('kp','') for k,v in mkf['feedback'].items() if v.get('cate','') =='e_spell' })
sum_spell_err	= lambda mkfs: sum([snt_spell_err(mkf) for mkf in mkfs])
has_grammar_err	= lambda mkf: any([k for k,v in mkf['feedback'].items() if v.get('cate','').startswith("e_")])
snt_kp_err		= lambda mkf: len({k for k,v in mkf['feedback'].items() if v.get('cate','').startswith("e_")})
